import React from "react";
import { Image, Text, TouchableOpacity} from "react-native";
import styles from "../bannerFilmes/style";
import style from "./style";




export default function CardFilmes(){
    return(
        <TouchableOpacity style = {style.containerFilmes}>
            <Image style = {style.imagemFilmes} source = {require(`../../Img/1.jpg`)}/>
            <Text style={style.tituloFilmes}> Ad√£o Negro </Text>
            <Text style={style.notaFilme}> 8.8 </Text>
        </TouchableOpacity>

    );
    
}
