import { StyleSheet } from "react-native";

const style = StyleSheet.create({

    containerFilmes :{
        paddingTop:10,
        paddingBottom:16,
        paddingRight: 16,
        width: 140

    },

    tituloFilmes :{
        color: '#fff',
        fontSize: 12,
        paddingTop: 8

    },

    notaFilme:{
        fontSize: 10,
        color: '#fff',
        paddingLeft: 4
    },

    imagemFilmes:{
        width: '100%',
        height: 170,
        borderRadius: 10
    }

    

})

export default style;
